mapper.select:
		SELECT c.*,d.dictName districtName,u.username userName FROM customer_info c  LEFT JOIN district d ON c.district_id = d.id LEFT JOIN sysuser u ON c.username_id = u.id  WHERE c.isdelete=0


            order by #{sort} #{order}


            limit #{start},#{end}

mapper.update:
	update customer

		name=#{name},
        address=#{address},
        zipcode=#{zipcode},
        fax=#{fax}

	where id = #{id}
